DROP TABLE IF EXISTS `xz_article`;^
CREATE TABLE `xz_article` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `title` varchar(255) NOT NULL,
  `description` text NOT NULL,
  `published` tinyint(1) NOT NULL default '0',
  `art_attr` tinyint(4) unsigned NOT NULL default '1',
  `sectionid` int(10) unsigned NOT NULL,
  `catid` int(10) unsigned NOT NULL,
  `created` datetime NOT NULL,
  `author` varchar(50) NOT NULL,
  `modified` datetime NOT NULL,
  `modified_by` varchar(50) NOT NULL,
  `publish_starttime` datetime NOT NULL,
  `publish_endtime` datetime NOT NULL,
  `hits` int(10) unsigned NOT NULL,
  `componentid` tinyint(3) unsigned NOT NULL,
  PRIMARY KEY  (`id`),
  KEY `sectionid` (`sectionid`)
) ENGINE=MyISAM AUTO_INCREMENT=138 DEFAULT CHARSET=utf8;^








































































































DROP TABLE IF EXISTS `xz_block`;^
CREATE TABLE `xz_block` (
  `id` int(30) unsigned NOT NULL auto_increment,
  `blockname` varchar(50) NOT NULL,
  `menuid` int(30) unsigned NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;^







DROP TABLE IF EXISTS `xz_category`;^
CREATE TABLE `xz_category` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `title` varchar(255) NOT NULL,
  `description` text NOT NULL,
  `published` tinyint(1) NOT NULL,
  `order` int(11) NOT NULL,
  `sectionid` int(10) unsigned NOT NULL,
  `componentid` tinyint(3) unsigned NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;^










DROP TABLE IF EXISTS `xz_comment`;^
CREATE TABLE `xz_comment` (
  `id` int(50) unsigned NOT NULL auto_increment,
  `art_id` int(50) unsigned NOT NULL,
  `name` varchar(50) NOT NULL,
  `comment` text NOT NULL,
  `created` datetime NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;^

DROP TABLE IF EXISTS `xz_component`;^
CREATE TABLE `xz_component` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `name` varchar(50) NOT NULL,
  `link` varchar(255) NOT NULL,
  `option` varchar(50) NOT NULL,
  `order` int(11) NOT NULL default '0',
  `enabled` tinyint(4) NOT NULL default '1',
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;^




DROP TABLE IF EXISTS `xz_link`;^
CREATE TABLE `xz_link` (
  `id` int(15) unsigned NOT NULL auto_increment,
  `link_name` varchar(50) NOT NULL,
  `link_url` varchar(100) NOT NULL,
  `published` tinyint(1) NOT NULL default '1',
  `created` datetime NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;^






DROP TABLE IF EXISTS `xz_login_record`;^
CREATE TABLE `xz_login_record` (
  `id` int(255) unsigned NOT NULL auto_increment,
  `username` varchar(50) NOT NULL,
  `email` varchar(50) NOT NULL,
  `login_date` datetime NOT NULL,
  `logout_date` datetime NOT NULL,
  `warn_count` int(25) unsigned NOT NULL default '0',
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;^










DROP TABLE IF EXISTS `xz_media`;^
CREATE TABLE `xz_media` (
  `id` int(50) unsigned NOT NULL auto_increment,
  `video_url` varchar(255) NOT NULL,
  `swf_url` varchar(255) NOT NULL,
  `description` varchar(255) NOT NULL default '''''',
  `create_date` datetime NOT NULL,
  `published` tinyint(2) unsigned NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;^


DROP TABLE IF EXISTS `xz_menu`;^
CREATE TABLE `xz_menu` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `title` varchar(255) NOT NULL,
  `menutype` varchar(75) NOT NULL,
  `description` varchar(255) NOT NULL,
  `published` tinyint(1) unsigned NOT NULL,
  PRIMARY KEY  (`id`),
  UNIQUE KEY `menutype` (`menutype`)
) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;^







DROP TABLE IF EXISTS `xz_menu_item`;^
CREATE TABLE `xz_menu_item` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `title` varchar(255) NOT NULL,
  `menuid` int(10) unsigned NOT NULL,
  `alias` varchar(255) NOT NULL,
  `link` text NOT NULL,
  `type` varchar(50) NOT NULL,
  `published` tinyint(1) NOT NULL default '0',
  `componentid` int(10) unsigned NOT NULL,
  `order` int(11) NOT NULL default '0',
  `type_id` int(10) unsigned NOT NULL,
  `image_src` varchar(50) NOT NULL,
  PRIMARY KEY  (`id`),
  KEY `type_id` (`type_id`)
) ENGINE=MyISAM AUTO_INCREMENT=29 DEFAULT CHARSET=utf8;^





























DROP TABLE IF EXISTS `xz_section`;^
CREATE TABLE `xz_section` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `title` varchar(255) NOT NULL,
  `description` text NOT NULL,
  `published` tinyint(1) NOT NULL default '0',
  `order` int(11) NOT NULL default '0',
  `componentid` tinyint(3) unsigned NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;^





DROP TABLE IF EXISTS `xz_user`;^
CREATE TABLE `xz_user` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `username` varchar(50) NOT NULL,
  `password` varchar(50) NOT NULL,
  `name` varchar(50) NOT NULL,
  `email` varchar(50) NOT NULL,
  `reg_date` datetime NOT NULL,
  `last_login_date` datetime NOT NULL,
  `active` tinyint(1) NOT NULL,
  PRIMARY KEY  (`id`),
  UNIQUE KEY `username` (`username`),
  UNIQUE KEY `email` (`email`),
  KEY `name` (`name`)
) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;^




